var nodemailer = require("nodemailer");


function sendEmail(host,email1,name1,email2,name2,email3,name3,email4,name4){

    var smtpTransport = nodemailer.createTransport("SMTP",{
	host: 'mail.gandi.net',
	port: 587,
	use_authentication: true,
	user: "geoffrey@starters-singapore.com",
	pass: "deutschebank"
    });

    smtpTransport.sendMail({
	from: "Geoffrey at Starters <geoffrey@starters-singapore.com>",
	to: "geoffrey@starters-singapore.com",
	subject: host + " invited friends",
	text: "name1: " + name1 + "\n\nemail1: " + email1 + "\n\nname2: " + name2 + "\n\nemail2: " + email2 + "\n\nname3: " + name3 + "\n\nemail3: " + email3 + "\n\nname4: " + name4 + "\n\nemail4: " + email4
    }, function(error, response){
	if(error){
	   console.log(error);
	} else{
	    console.log("Success!");
	}
    });
}

exports.sendEmail = sendEmail;
